mapper.select:
/* issueChecker.getIssueList */ SELECT ISSUEID , SUBJECT , CONTENTS , TYPE , STATE , (SELECT CDNM FROM T_COMMONCODE WHERE GRPCD='TYPE' AND TYPE = CD) TYPENM , (SELECT CDNM FROM T_COMMONCODE WHERE GRPCD='STATE' AND TYPE = CD) STATENM , REGISTERID , CHARGEPERSONID , CREATIONDATE , MODIFICATIONDATE FROM T_ISSUE
AND TYPE = #{type}
AND STATE = #{state}
AND REGISTERID = #{registerId}
AND CHARGEPERSONID = #{chargePersonId}

mapper.delete:
        /* issueChecker.deleteIssue */
		DELETE 
		FROM T_ISSUE
		WHERE
			ISSUEID = #{issueId}

mapper.select:
		/* issueChecker.getLoginUser */
    	SELECT USERID,LOGINID,USERNAME,EMAIL
    	FROM T_USER


    			LOGINID = #{loginId}
