mapper.insert:
INSERT INTO CATEGORY (MEMBERID, DEVICEID, CATEGORYID, NAME, SORTORDER, ISETC, ISSERVIVE, ISEDITABLE )
VALUES (#{memberid}, 0, #{categoryid}, #{name}, #{sortorder,jdbcType=VARCHAR}, #{isetc ,jdbcType=VARCHAR}, #{isservice,jdbcType=VARCHAR}, #{iseditable,jdbcType=VARCHAR} )
)

mapper.update:
UPDATE CATEGORY
SET    
       
       NAME             = #{name},
       SORTORDER        = #{sortorder}
WHERE  MEMBERID         = #{memberid}
AND    DEVICEID         = #{deviceid}
AND    CATEGORYID       = #{categoryid}
