mapper.select:
/* posorder.getTableOrderPresentList */ SELECT A.MEMBERID ,A.DEVICEID ,A.TABLEID ,A.TABLENAME ,A.LINKEDTABLEID ,A.ISDELETED ,B.ORDERID ,B.CREATIONDATE ,to_char(B.CREATIONDATE,'yyyymmdd hh24:mi:ss') CREATIONDATE_STR ,B.MODIFICATIONDATE ,(SELECT NVL(SUM(NVL(PRICE, 0)*NVL(QUANTITY, 0)), 0) FROM ORDERDETAIL WHERE ISDELETED = 'N' AND ISSERVICE != 'Y' AND MEMBERID = #{memberId} AND TABLEID = A.TABLEID) AS SUM_PRICE FROM ORDERTABLE A LEFT OUTER JOIN TABLEORDER B ON A.TABLEID = B.TABLEID AND A.MEMBERID = B.MEMBERID AND B.ISPAID = 'N' AND B.ISDELETED = 'N' WHERE A.MEMBERID = #{memberId}
AND A.ISDELETED = #{isDeleted}
ORDER BY ${orderBy} ${orderOption}
ORDER BY A.TABLEID ASC

