mapper.update:
UPDATE TABLEORDER
SET
	MODIFICATIONDATE = SYSDATE      
        
            ,CUSTOMERID = #{customerId}
          
        
            ,PRICE = #{price}
                                   
        
            ,GIVEBACKCREDIT = #{givebackcredit}
                     
        
            ,LOSS = #{loss}
               
        
            ,DISCOUNT = #{discount}
              
        
            ,BILLMONEY = #{billmoney}
             
        
            ,CASHPAYMENT = #{cashpayment}
                
        
            ,CARDPAYMENT = #{cardpayment}
           
        
            ,INTOCREDIT = #{intocredit}
             
        
            ,INTOLOSS = #{intoloss}
            
        
            ,INTODEPOSIT = #{intodeposit}
             
        
            ,USEDTICKET = #{usedticket}
          
        
            ,SAVEDPOINT = #{savedpoint}
                 
        
            ,USEDDEPOSIT = #{useddeposit}
                  
        
            ,USEDPOINT = #{usedpoint}
          
        
            ,ISCHANGEAFTERMERGE = #{ischangeaftermerge}
             
        
            ,ISDELETED = #{isdeleted}
             
        
            ,ISPAID = #{ispaid}
          
        
            ,ISTAKEOUTORDER = #{istakeoutorder}
        
WHERE
	ORDERID = #{orderId}

mapper.insert:
        UPDATE ORDERDETAIL
		SET
			QUANTITY = #{quantity}
			,ISTAKEOUT = #{isTakeout}
			,ORIGINALPRICE = #{originalPrice}
            ,DISCOUNTPRICE = #{discountPrice} 
			,ISSERVICE = #{isService}               
            
                ,ISDELETED = #{isdeleted}
            
            ,MODIFICATIONDATE = SYSDATE
		WHERE
			ORDERDETAILID = #{orderDetailId}

mapper.select:
/* posorder.getOrderDetailList */ SELECT A.MEMBERID ,A.DEVICEID ,A.ORDERDETAILID ,A.ORDERID ,A.TABLEID ,A.MENUID ,A.QUANTITY ,A.ISTAKEOUT ,A.ORIGINALPRICE ,A.DISCOUNTPRICE ,A.ISSERVICE ,A.ISDELETED ,A.CREATIONDATE ,A.MODIFICATIONDATE ,B.CATEGORYID FROM ORDERDETAIL A JOIN MENU B ON A.MENUID = B.MENUID WHERE A.MEMBERID = #{memberId} AND A.ORDERID = #{orderId}
AND A.ISDELETED = #{isDeleted}
AND A.ISDELETED = 'N'
ORDER BY ${orderBy} ${orderOption}
ORDER BY A.CREATIONDATE ASC
