mapper.insert:
        /* posspecial.insertBeforeMerge */
		INSERT INTO BEFOREMERGE
		(
			MEMBERID
			,DEVICEID
			,BEFOREMERGEID
			,NEWORDERID
			,ORDERID
			,SUBORDERID
			,ISDELETED
			,CREATIONDATE
			,MODIFICATIONDATE
		) VALUES (
			#{memberId}
			,#{deviceId}
			,#{beforeMergeId}
			,#{newOrderId}
			,#{orderId}
			,#{subOrderId}
			,#{isDeleted}
			,SYSDATE
			,SYSDATE
		)

mapper.select:
/* posspecial.getBeforeMerge */ SELECT MEMBERID ,DEVICEID ,BEFOREMERGEID ,NEWORDERID ,ORDERID ,SUBORDERID ,ISDELETED ,CREATIONDATE ,MODIFICATIONDATE FROM BEFOREMERGE WHERE
BEFOREMERGEID = #{beforeMergeId}
NEWORDERID = #{newOrderId}
AND ISDELETED = #{isDeleted}

mapper.delete:
        /* posspecial.deleteBeforeMerge */
		DELETE FROM BEFOREMERGE		
		WHERE
			BEFOREMERGEID = #{beforeMergeId}
