mapper.select:
/* posorder.getTableOrderPresentList */ SELECT A.MEMBERID ,A.DEVICEID ,A.TABLEID ,A.TABLENAME ,A.TABLENO ,A.LINKEDTABLEID ,C.TABLENAME AS LINKEDTABLENAME ,A.ISDELETED ,B.ORDERID ,B.CREATIONDATE ,to_char(B.CREATIONDATE,'yyyymmdd hh24:mi:ss') CREATIONDATE_STR ,B.MODIFICATIONDATE ,(SELECT NVL(SUM(NVL(DISCOUNTPRICE, 0)), 0) FROM ORDERDETAIL WHERE TABLEID = A.TABLEID AND ORDERID = B.ORDERID) AS SUM_PRICE ,(SELECT COUNT(*) FROM ORDERDETAIL WHERE TABLEID = A.TABLEID AND ORDERID = B.ORDERID AND ISTAKEOUT = 'Y') AS TAKEOUTCOUNT ,(SELECT COUNT(*) FROM ORDERDETAIL WHERE TABLEID = A.TABLEID AND ORDERID = B.ORDERID AND ISSERVICE = 'Y') AS SERVICECOUNT ,(SELECT COUNT(*) FROM MEMO WHERE STARTSALESDATE = to_char(sysdate - (6/24), 'YYYYMMDD') AND TABLEID = A.TABLEID AND MEMOTYPE = 'CC00001001' AND (ORDERID IS NULL OR ORDERID = B.ORDERID)) AS MEMO_COUNT ,(SELECT COUNT(*) FROM MEMO WHERE STARTSALESDATE = to_char(sysdate - (6/24), 'YYYYMMDD') AND TABLEID = A.TABLEID AND MEMOTYPE = 'CC00001004' AND ORDERID = B.ORDERID AND CONTENTS != '나갈 때 대리운전 콜 부탁') AS CUSTOMERREQUEST_COUNT ,(SELECT COUNT(*) FROM MEMO WHERE STARTSALESDATE = to_char(sysdate - (6/24), 'YYYYMMDD') AND TABLEID = A.TABLEID AND MEMOTYPE = 'CC00001004' AND ORDERID = B.ORDERID AND CONTENTS = '나갈 때 대리운전 콜 부탁') AS DAERI_COUNT ,(SELECT COUNT(*) FROM ORDERTABLE WHERE LINKEDTABLEID = A.TABLEID AND ISDELETED = 'N') AS LINKEDTABLECOUNT FROM ORDERTABLE A LEFT OUTER JOIN TABLEORDER B ON A.TABLEID = B.TABLEID AND A.MEMBERID = B.MEMBERID AND B.ISPAID = 'N' AND B.ISDELETED = 'N' LEFT OUTER JOIN ORDERTABLE C ON A.LINKEDTABLEID = C.TABLEID WHERE A.MEMBERID = #{memberId}
AND A.ISDELETED = #{isDeleted}
ORDER BY ${orderBy} ${orderOption}
ORDER BY A.TABLENO ASC

