mapper.insert:
        /* posspecial.insertBeforeMerge */
		INSERT INTO BEFOREMERGE
		(
			MEMBERID
			,DEVICEID
			,BEFOREMERGEID
			,NEWORDERID
			,ORDERID
			,SUBORDERID
			,ISDELETED
			,CREATIONDATE
			,MODIFICATIONDATE
		) VALUES (
			#{memberId}
			,#{deviceId}
			,#{beforeMergeId}
			,#{newOrderId}
			,#{orderId}
			,#{subOrderId}
			,#{isDeleted}
			,SYSDATE
			,SYSDATE
		)

mapper.select:
/* posspecial.getBeforeMerge */ SELECT MEMBERID ,DEVICEID ,BEFOREMERGEID ,NEWORDERID ,ORDERID ,SUBORDERID ,ISDELETED ,CREATIONDATE ,MODIFICATIONDATE FROM BEFOREMERGE WHERE
BEFOREMERGEID = #{beforeMergeId}
NEWORDERID = #{newOrderId}
AND ISDELETED = #{isDeleted}

mapper.delete:
        /* posspecial.deleteBeforeMerge */
		DELETE FROM BEFOREMERGE		
		WHERE
			BEFOREMERGEID = #{beforeMergeId}

mapper.update:
        /* posspecial.updateOrderTable */
		UPDATE ORDERTABLE
		SET
			MODIFICATIONDATE = SYSDATE
			
			,TABLENO = #{tableNo}
			
			
			,TABLENAME = #{tableName}
			
			
			,LINKEDTABLEID = #{linkedTableId}
			
			
			,ISDELETED = #{isDeleted}
					
		WHERE
			TABLEID  = #{tableId}
		AND MEMBERID = #{memberId}
		AND DEVICEID = #{deviceId}
